import { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { HashRouter, Routes, Route } from 'react-router-dom'
import { Container } from 'react-bootstrap'
import { ethers } from 'ethers'

import dappswap from '../dapp-swap.png';
import apple from '../apple.jpeg';

// Components
import Navigation from './Navigation';
import Tabs from './Tabs';
import Swap from './Swap';
import Deposit from './Deposit';
import Withdraw from './Withdraw';
import Charts from './Charts';

import {
  loadProvider,
  loadBalances1,
  loadNetwork,
  loadAccount,
  loadTokens,
  loadAMM
} from '../store/interactions'

function App() {

  const [amm, setAMM] = useState(0)

  const [dappAMM, setDappAMM] = useState(null)
  const [appleAMM, setAppleAMM] = useState(null)


  const token1 = useSelector(state => state.amm.token1)
  const token2 = useSelector(state => state.amm.token2)

  const token3 = useSelector(state => state.appleswap.token3)
  const token4 = useSelector(state => state.appleswap.token4)



  const dispatch = useDispatch()

  const connectHandler = async () => {
    const account = await loadAccount(dispatch)
    await loadBalances(appleswap, tokens, account, dispatch)
  }

  const loadBlockchainData = async () => {

    // Initiate provider
    const provider = await loadProvider(dispatch)

    // Fetch current network's chainId (e.g. hardhat: 31337, kovan: 42)
    const chainId = await loadNetwork(provider, dispatch)

    // Reload page when network changes
    window.ethereum.on('chainChanged', () => {
      window.location.reload()
    })

    // Fetch current account from Metamask when changed
    window.ethereum.on('accountsChanged', async () => {
      await loadAccount(dispatch)
    })

    // Initiate contracts
    await loadTokens(provider, chainId, dispatch)
    await loadAMM(provider, chainId, dispatch)

    setDappAMM('0xA51c1fc2f0D1a1b8494Ed1FE312d7C3a78Ed91C0')
    setAppleAMM('0x0DCd1Bf9A1b36cE34237eEaFef220932846BCD82')

  }


  useEffect(() => {
    loadBlockchainData()
  }, []);

  return(
    <Container>

    <style>{'body { background-color: rgb(20, 240, 120); opacity: 0.9; }'}</style>

      <HashRouter>

        <Navigation />

        <hr />

        <h6 className='my-4 text-left'>Total DAPP on DApp Swap: <strong>{parseFloat(token1).toFixed(2)}</strong> tokens</h6>
        <h6 className='my-4 text-left'>Total USD on DApp Swap: <strong>{parseFloat(token2).toFixed(2)}</strong> tokens</h6>

        <h6 className='my-4 text-left text-danger'>Total DAPP on Apple Swap: <strong>{parseFloat(token3).toFixed(2)}</strong> tokens</h6>
        <h6 className='my-4 text-left text-danger'>Total USD on Apple Swap: <strong>{parseFloat(token4).toFixed(2)}</strong> tokens</h6>

        <Tabs />

        <Routes>
          <Route exact path="/" element={<Swap />} />
          <Route path="/charts" element={<Charts />} />
        </Routes>

        <h3 className='my-4 text-center'>Participating Exchanges:</h3>

        <h6 className='my-4 text-center p-3 mb-2 bg-danger bg-gradient rounded-5 text-white'>       

        <img
        alt="dappswap"
        src={dappswap}
        width="40"
        height="40"
        className="align-right mx-3"
        />

        DApp Swap: <strong>{dappAMM}</strong></h6> 


        <h6 className='my-4 text-center p-3 mb-2 bg-danger bg-gradient rounded-5 text-white'>

        <img
        alt="apple"
        src={apple}
        width="40"
        height="40"
        className="align-right mx-3"
        />

        Apple Swap: <strong>{appleAMM}</strong></h6>

      </HashRouter>
    </Container>
  )
}

export default App;
